import React, { useContext } from 'react';
import Headroom from 'react-headroom';
import './Header.css';
import ToggleSwitch from '../ToggleSwitch/ToggleSwitch';
import StyleContext from '../../contexts/StyleContext';
import {
  greeting,
  workExperiences,
  skillsSection,
  openSource,
  blogSection,
  // talkSection,
  achievementSection,
  educationInfo
} from '../../portfolio';

function Header() {
  const { isDark } = useContext(StyleContext);
  const viewExperience = workExperiences.display;
  const viewOpenSource = openSource.display;
  const viewSkills = skillsSection.display;
  const viewAchievement = achievementSection.display;
  const viewBlog = blogSection.display;
  const education = educationInfo.display;
  // const viewTalks = talkSection.display;

  return (
    <Headroom>
      <header className={isDark ? 'dark-menu header' : 'header'}>
        <a href="" className="logo">
          <span className="grey-color"> &lt;</span>
          <span className="logo-name">{greeting.username}</span>
          <span className="grey-color"> /&gt;</span>
        </a>
        <input className="menu-btn" type="checkbox" id="menu-btn" />
        <label
          className="menu-icon"
          htmlFor="menu-btn"
          style={{ color: 'white' }}
        >
          <span className={isDark ? 'navicon navicon-dark' : 'navicon'} />
        </label>
        <ul className={isDark ? 'dark-menu menu' : 'menu'}>
          {viewSkills && (
            <li>
              <a href="#skills">Conocimientos</a>
            </li>
          )}
          {education && (
            <li>
              <a href="#education">Educaci√≥n</a>
            </li>
          )}
          {viewExperience && (
            <li>
              <a href="#experience">Experiencia</a>
            </li>
          )}
          {viewAchievement && (
            <li>
              <a href="#achievements">Cursos</a>
            </li>
          )}
          {viewOpenSource && (
            <li>
              <a href="#opensource">Proyectos</a>
            </li>
          )}

          {/* {viewTalks && ( */}
          {/*  <li> */}
          {/*    <a href="#talks">Talks</a> */}
          {/*  </li> */}
          {/* )} */}
          <li>
            <a href="#contact">Contacto</a>
          </li>
          <li>
            <a>
              <ToggleSwitch />
            </a>
          </li>
        </ul>
      </header>
    </Headroom>
  );
}
export default Header;
